INSERT INTO `dishes` (`id`, `dish_name`, `dish_price`, `dish_description`, `total_order`, `menu_id`, `discount_id`, `created_at`, `updated_at`) VALUES
(1, 'Trà Sữa Truyền Thống', 20000, 'Trà sữa, loại thức uống vô cùng đặc biệt với thành phần chính vẻn vẹn ngay từ chính tên gọi của nó. Là gồm nước trà và sữa hòa quyện vào nhau. Loại thức uống đã làm nên một nét văn hóa mới trong thời hiện đại, đó là Văn hóa trà sữa.\r\n', 0, 2, NULL, '2023-11-20 08:52:27', '2023-12-02 02:43:34'),
(2, 'Trà Đào', 20001, 'Trà đào, loại thức uống vô cùng đặc biệt với thành phần chính vẻn vẹn ngay từ chính tên gọi của nó. Là gồm nước trà và đào hòa quyện vào nhau. \r\n', 1, 2, NULL, '2023-11-20 08:53:59', '2023-12-02 10:38:37'),
(3, 'Cơm Tấm Sườn Bì Chả', 70000, 'aido ngon quá mời bạn ăn nha, tôi đây không chờ bạn nữa giờ tôi ăn liền cơm tấm sườn bì chả nha.', 1, 3, NULL, '2023-11-20 08:54:45', '2023-12-03 03:36:28'),
(4, 'Bún Chả Hà Nội', 50000, 'Bún chả Hà Nội là một món ăn truyền thống đặc sản của thủ đô Việt Nam, nổi tiếng với sự kết hợp tinh tế giữa bún mềm mịn, chả thơm ngon và nước mắm pha chua ngọt độc đáo.', 1, 3, NULL, '2023-11-20 08:55:29', '2023-12-03 03:38:29'),
(5, 'Bánh Mì', 60000, 'Bánh mì có thể được ăn kèm với nhiều loại nhân khác nhau, bao gồm thịt, cá, rau, trứng, phô mai, pate và nhiều loại gia vị khác. Mỗi loại nhân mang đến hương vị độc đáo và tạo nên sự phong phú trong lựa chọn.', 0, 3, NULL, '2023-11-20 08:56:02', '2023-12-02 02:46:14'),
(6, 'chè dừa dầm', 30000, 'chè ngon SG.', 1, 1, NULL, '2023-11-20 08:56:32', '2023-12-02 10:38:38'),
(7, 'rau câu dừa', 35000, 'rau câu ngon SG.', 1, 1, NULL, '2023-11-20 08:57:03', '2023-12-02 10:38:38'),
(15, 'Coca Cola', 15000, 'Coca-Cola nổi tiếng với hương vị đặc trưng, kết hợp giữa độ ngọt hoàn hảo và độ carbonation nhẹ nhàng. Mỗi ngụm mang đến cảm giác sảng khoái, tạo nên trải nghiệm thưởng thức độc đáo và khó quên.', 0, 2, NULL, '2023-12-01 18:41:46', '2023-12-01 18:41:46'),
(17, 'Strongbow', 25000, 'Trải nghiệm hương vị táo tươi mát và sảng khoái với Strongbow Original. Hòa quyện giữa độ ngọt và hậu vị khó quên, đây là lựa chọn hoàn hảo cho những người thích sự truyền thống và đồng thời muốn thưởng thức hương vị hiện đại.', 0, 2, NULL, '2023-12-01 18:45:10', '2023-12-01 18:45:10'),
(18, 'Rượu Soju', 89000, 'Với hương vị mềm mại và truyền thống, Soju là sự kết hợp hoàn hảo của gạo và lúa mạch, tạo nên một loại rượu nhẹ nhàng và dễ uống. Mỗi giọt Soju đều là một chuyến phiêu lưu qua vị giác, từ sự ngọt ngào đến hậu vị êm dịu.', 0, 2, NULL, '2023-12-01 18:46:37', '2023-12-01 18:46:37'),
(19, 'Tiger Soju', 39000, 'Tiger Soju tự hào mang đến hương vị tinh tế và độ truyền thống của rượu Soju, với sự kết hợp hoàn hảo giữa độ mềm mại và đậm đà. Hương vị của Tiger Soju là sự hòa quyện giữa các thành phần chọn lọc, tạo nên một trải nghiệm thưởng thức độc đáo.', 0, 2, NULL, '2023-12-01 18:47:57', '2023-12-01 18:47:57'),
(20, 'Cocktail B52', 139000, 'Một sự kết hợp hoàn hảo giữa rượu rum, đường, chanh và lá bạc hà tạo nên Mojito - một cocktail tươi mát và sảng khoái. Hương thơm của lá bạc hà, chua chua ngọt ngọt của chanh và vị ngọt dịu của đường tạo nên một trải nghiệm thư giãn và phóng khoáng.', 0, 2, NULL, '2023-12-01 18:49:29', '2023-12-01 18:49:29'),
(21, 'Pepsi', 19000, 'Pepsi là thương hiệu nổi tiếng và phổ biến trên toàn thế giới. Đồ uống này có một hương vị đặc trưng, kết hợp giữa hương vị cola đậm đà và cảm giác sảng khoái từ bọt ga. ', 0, 2, NULL, '2023-12-01 18:53:39', '2023-12-01 18:53:39'),
(22, 'Trà chanh', 19000, 'Với một hòa quyện độc đáo giữa hương thơm tự nhiên của lá trà và vị chua ngọt của chanh, trà chanh tạo nên một sự cân bằng hoàn hảo. Hương vị thanh mát của trà kết hợp với hương thơm tự nhiên và vị chua nhẹ của chanh, tạo ra một ly trà chanh mà bạn không thể cưỡng lại.', 0, 2, NULL, '2023-12-01 18:55:25', '2023-12-01 18:55:25'),
(23, 'Bún Bò Huế', 69000, 'Bún Bò Huế là một món ăn đặc trưng của Huế, miền Trung Việt Nam, và chúng tôi tự hào giới thiệu đến bạn một phiên bản tuyệt vời của món ăn này. Bún Bò Huế là một tô bún mềm mịn, được nấu trong nước dùng thơm ngon và đậm đà, kết hợp với thịt bò thăn mềm và các loại gia vị đặc trưng.\r\n\r\nNước dùng của Bún Bò Huế được làm từ xương hầm, hòa quyện với các loại gia vị như sả, hành, tỏi, ớt và một số loại gia vị khác. Đây là yếu tố quan trọng tạo nên hương vị đặc biệt và hấp dẫn của món ăn. Thịt bò được nấu chín mềm mịn, giòn ngọt, tạo nên một sự kết hợp hoàn hảo với bún và nước dùng.', 0, 3, NULL, '2023-12-01 18:57:39', '2023-12-01 18:57:39'),
(24, 'Bún Mắm Nêm', 49000, 'Bún Mắm Nêm là một món ăn truyền thống của miền Nam Việt Nam, có hương vị đặc trưng và hấp dẫn.\r\n\r\nBún Mắm Nêm bao gồm một tô bún mềm mịn, được nấu trong nước mắm tươi ngon và được gia vị nêm từ các loại cá và tôm tươi. Nước mắm nêm có hương vị thơm ngon, đậm đà và cay nồng, mang đến một trải nghiệm thưởng thức độc đáo.\r\n\r\nPhụ gia vào Bún Mắm Nêm là các thành phần khác như thịt heo nướng, chả giò, tôm, giò lụa, rau sống, giá đỗ, hành, ớt và các loại gia vị khác. Những thành phần này tạo ra một cảm giác thịt thơm ngon, giòn ngọt và đa dạng trong khẩu vị khi kết hợp với bún và nước mắm nêm.', 0, 3, NULL, '2023-12-01 18:58:46', '2023-12-01 18:58:46'),
(25, 'Bún Riêu Cua', 69000, 'Bún Riêu Cua là một món ăn ngon và đậm đà, sẽ mang đến cho bạn một trải nghiệm ẩm thực tuyệt vời.Nước dùng có màu đỏ tươi sáng và hương vị đặc trưng, tạo nên một lớp màu sắc và hương thơm hấp dẫn cho món ăn.\r\n\r\nBên trong tô bún, bạn sẽ tìm thấy các viên riêu cua mềm mịn và thơm ngon. Riêu cua được làm từ cua tươi, được xay nhuyễn và chế biến với các loại gia vị tự nhiên. Thịt cua tạo ra một hương vị đậm đà và đa dạng, kết hợp hoàn hảo với bún và nước dùng.', 0, 3, NULL, '2023-12-01 18:59:57', '2023-12-01 18:59:57'),
(26, 'Bún Đậu Mắm Tôm', 79000, 'Bún Đậu Mắm Tôm là một món ăn truyền thống của miền Bắc Việt Nam, gồm bún mềm mịn, đậu tương, mắm tôm và các thành phần phụ như chả, nem chua, thịt luộc và rau sống. Món ăn mang hương vị đặc trưng, cay nồng và thường được kèm theo nước mắm pha chua ngọt và gia vị.', 0, 3, NULL, '2023-12-01 19:01:58', '2023-12-01 19:01:58'),
(27, 'Bún Thịt Nướng', 69000, 'Bún Thịt Nướng là một món ăn phổ biến trong ẩm thực Việt. Nó bao gồm những sợi bún mềm mịn kết hợp với thịt heo hoặc thịt bò nướng thơm ngon. Thịt được ướp gia vị trước khi nướng, tạo ra hương vị đậm đà và màu sắc hấp dẫn. Bún Thịt Nướng thường được phục vụ với rau sống, bắp chuối, dưa leo, và nước mắm pha chua ngọt', 0, 3, NULL, '2023-12-01 19:03:31', '2023-12-01 19:03:31'),
(28, 'Cơm Tấm', 50000, 'Cơm Tấm là một món ăn phổ biến và đặc trưng trong ẩm thực Việt Nam. Bao gồm cơm trắng được nén kết hợp với các thành phần như thịt nướng, trứng, mỡ hành, và dưa leo. Thịt nướng được ướp gia vị và nướng đến khi thơm ngon và màu sắc hấp dẫn. Cơm Tấm thường được kèm theo một số gia vị như mắm nêm hoặc nước mắm pha chua ngọt. Món ăn này mang đến hương vị đậm đà, cân bằng giữa vị mặn, ngọt và chua, tạo nên một bữa ăn đơn giản nhưng ngon miệng.', 0, 3, NULL, '2023-12-01 19:04:42', '2023-12-01 19:04:42'),
(29, 'Cơm Cà Ri Nhật', 59000, 'Cơm Cà Ri Nhật là một món ăn pha trộn giữa hương vị cà ri từ Ấn Độ và phong cách Nhật Bản. Món ăn này bao gồm cơm trắng được kết hợp với cà ri Nhật, có hương vị nhẹ nhàng và tinh tế. Cà ri Nhật thường có thành phần chính là thịt gà, thịt bò hoặc hải sản, được nấu chín cùng với các loại rau củ và gia vị đặc trưng. Cơm Cà Ri Nhật mang đến một hương vị độc đáo, pha trộn giữa mùi thơm của cà ri và hương vị tinh tế của ẩm thực Nhật Bản.', 0, 3, NULL, '2023-12-01 19:05:52', '2023-12-01 19:05:52'),
(30, 'Lẩu Cua Đồng', 90000, 'Lẩu cua đồng được chế biến từ cua đồng tươi ngon. Cua đồng được nấu chín trong nồi lẩu với nước dùng thơm ngon và gia vị tạo nên hương vị đậm đà. Ăn kèm theo các loại rau sống, mì hoặc bún, tạo thành một bữa ăn ngon miệng và hấp dẫn. Lẩu Cua Đồng mang đến hương vị đặc trưng của cua đồng tươi ngon, khiến món ăn trở thành một điểm nhấn đặc biệt trong menu của quán ăn.', 0, 3, NULL, '2023-12-01 19:07:57', '2023-12-01 19:07:57'),
(31, 'Beef Hokubee', 419000, 'Beef Hokubee là một món ăn đặc sản của Nhật Bản, thường có mặt trong menu của các quán ăn Nhật. Món này bao gồm miếng thịt bò nướng mềm mịn, được chế biến với phong cách đặc trưng của Nhật Bản. Thịt bò được nướng chín vừa, giữ được độ mềm và ngon. Beef Hokubee thường được chế biến với các loại gia vị như đậu phộng rang, hành tây, tỏi, và nước sốt đặc biệt. Món ăn này mang đến hương vị đậm đà, kết hợp giữa vị ngọt, mặn, chua và cay, tạo nên một trải nghiệm ẩm thực độc đáo.', 0, 3, NULL, '2023-12-01 19:10:45', '2023-12-01 19:10:45'),
(32, 'Bò Xào Cần Tỏi', 59000, 'Bò Xào Cần Tỏi là một món ăn phổ biến trong ẩm thực Việt Nam. Món này được chế biến từ thịt bò xào nhanh cùng với tỏi và các loại rau cần tươi ngon. Thịt bò thường được thái mỏng và xào chín vừa để giữ được độ mềm và mềm mịn. Tỏi được thêm vào để tạo thêm hương vị đậm đà và thơm ngon. Bò Xào Cần Tỏi thường được phục vụ cùng với cơm trắng hoặc bánh mì, tạo nên một bữa ăn ngon miệng và hấp dẫn.', 0, 3, NULL, '2023-12-01 19:12:38', '2023-12-01 19:12:38'),
(33, 'Beef Steak', 359000, 'Beef Stack bao gồm việc xếp chồng các lớp thịt bò nướng mềm mịn, tạo thành một cấu trúc đẹp mắt và hấp dẫn. Thịt bò thường được nướng đến khi chín vừa, giữ được độ mềm và ngon. Beef Stack thường được kết hợp với các loại rau sống, khoai tây chiên hoặc các món phụ khác để tạo nên một bữa ăn hoàn chỉnh và thỏa mãn khẩu vị.', 0, 3, NULL, '2023-12-01 19:14:18', '2023-12-01 19:14:18'),
(34, 'Lẩu Bò', 418000, 'Lẩu bò bao gồm nồi lẩu có nước dùng thơm ngon và thành phần chính là thịt bò. Thịt bò thường được thái mỏng để nhanh chóng nấu chín trong nước lẩu. Ngoài thịt bò, lẩu bò thường được kết hợp với các loại rau, nấm, hải sản hoặc các loại bún, miến tùy theo sở thích. Món lẩu này mang đến hương vị đậm đà, cân bằng giữa vị mặn, ngọt và hương thơm của các thành phần khác nhau. Lẩu Bò là một lựa chọn tuyệt vời để thưởng thức bữa ăn ấm cúng và hấp dẫn cùng gia đình và bạn bè.', 0, 3, NULL, '2023-12-01 19:15:46', '2023-12-01 19:15:46'),
(36, 'Bò Hàu Xốt Phô Mai', 379000, 'Bò hàu xốt phô mai bao gồm hàu tươi sống được chế biến với sốt phô mai béo ngậy. Hàu thường được nướng hoặc hấp chín vừa để giữ được độ tươi ngon và mềm mịn. Sốt phô mai được tạo thành từ phô mai tan chảy, mang đến hương vị đặc trưng và béo ngậy. Bò hàu sốt phô mai được trình bày trên đĩa và ăn kèm với bánh mì hoặc bánh mì nướng để tạo nên sự kết hợp hài hòa giữa vị ngọt, mặn và béo của phô mai cùng với hương vị đặc biệt của bò và hàu. ', 0, 3, NULL, '2023-12-01 19:20:04', '2023-12-01 19:20:04'),
(37, 'Bò Nướng Tảng', 279000, 'Bò nướng tảng là một món ăn thú vị trong menu quán ăn, bao gồm miếng thịt bò tươi ngon được nướng trên một tảng đá nóng. Thịt bò thường được thái mỏng và nướng chín vừa để giữ được độ mềm và mùi thơm tự nhiên. Tảng đá nóng giúp tiếp tục nhiệt độ nướng và giữ cho thịt bò ấm hơn trong suốt quá trình ăn. Bò nướng tảng thường được phục vụ cùng với các loại nước sốt hoặc gia vị để tăng thêm hương vị. Món ăn tạo nên một trải nghiệm ẩm thực độc đáo và thú vị cho thực khách, đặc biệt là khi thưởng thức cùng với đồ ăn kèm như rau sống, bánh mì hoặc các món phụ khác.', 0, 3, NULL, '2023-12-01 19:21:41', '2023-12-01 19:21:41'),
(38, 'Kem Dừa', 20000, 'Kem dừa là một món tráng miệng ngon lành và mát lạnh. Được làm từ nước cốt dừa tươi tự nhiên, kem dừa có vị thơm ngọt và béo ngậy.', 0, 1, NULL, '2023-12-01 19:23:13', '2023-12-01 19:23:28'),
(39, 'Bánh Flan', 20000, 'Bánh flan là một món tráng miệng truyền thống và phổ biến trong nhiều nền văn hóa. Nó được làm từ trứng, đường, sữa và một chút vani để tạo nên một cấu trúc mềm mịn và hương vị thơm ngọt. Bánh flan thường có một lớp caramel màu nâu ở phía dưới, tạo nên một hương vị đặc trưng và màu sắc hấp dẫn.', 0, 1, NULL, '2023-12-01 19:24:25', '2023-12-01 19:24:25'),
(40, 'Kem Sầu Riêng', 20000, 'Được làm từ trái sầu riêng, kem này có vị ngọt, thơm và béo ngậy đặc trưng của trái cây sầu riêng. Kem sầu riêng thường được làm từ nước cốt sầu riêng và sữa, kết hợp với đường và kem tươi để tạo nên một kết cấu mịn màng và kem mềm', 0, 1, NULL, '2023-12-01 19:25:21', '2023-12-01 19:25:21'),
(41, 'Kem Chocolate', 30000, 'Kem chocolate là một món tráng miệng phổ biến và được yêu thích trên khắp thế giới. Kem này có thành phần chính là sô-cô-la, tạo nên vị ngọt, đậm đà và hương thơm đặc trưng của sô-cô-la. Kem chocolate thường được làm từ sữa, đường và sô-cô-la nướng.', 0, 1, NULL, '2023-12-01 19:26:13', '2023-12-01 19:26:13'),
(42, 'Kem Ốc Quế', 30000, 'Được làm từ một hỗn hợp của kem vani và gia vị ốc quế, kem ốc quế mang đến hương vị ngọt ngào và thơm lừng của quế.', 0, 1, NULL, '2023-12-01 19:27:31', '2023-12-01 19:27:31'),
(43, 'Chè Hạt Sen', 30000, 'Chè hạt sen là một món tráng miệng truyền thống và phổ biến trong ẩm thực Việt Nam.  Được làm từ hạt sen tươi và các thành phần khác như đường, nước cốt dừa, và một số loại thảo dược. Chè hạt sen có hương vị ngọt nhẹ và mát lành, với hạt sen mềm mịn và thường có màu trắng trong suốt.', 0, 1, NULL, '2023-12-01 19:28:42', '2023-12-01 19:28:42'),
(44, 'Rau Câu Thập Cẩm', 29000, 'Được làm từ gelatin, đường, nước và các loại hương vị tự nhiên, rau câu thập cẩm có màu sắc đa dạng và hình dáng đẹp mắt. Rau câu thập cẩm được làm thành các lớp màu khác nhau, tạo nên một hình ảnh thị giác thú vị. Rau câu thập cẩm có kết cấu mềm mịn, nhẹ nhàng và là một lựa chọn tuyệt vời trong những ngày nóng.', 0, 1, NULL, '2023-12-01 19:30:52', '2023-12-02 16:39:01');
